#!/usr/bin/env node

var config = {
  glfw: {
    title: "An OpenGL application",
    windowSize: [ 800, 600 ],
    version: [ 4, 1 ],
    sampleRate: 0,
    flags: {
      visible: true,
      fullscreen: false,
      vsync: false,
      cursor: true,
      stereo: false,
      debug: true
    }
  }
};

for (var i = 2; i < process.argv.length; i++) {
  require(process.argv[i]).configure(config);
}

console.log(JSON.stringify(config));
